function updateStoryFile(filePath) {
    try {
        const content = fs.readFileSync(filePath, 'utf8');
        const story = JSON.parse(content);
        
        // Check if summary exists and is a string
        if (story.summary && typeof story.summary === 'string') {
            const sourceText = story.summary;
            const targetText = translations[sourceText];
            
            if (targetText) {
                // Update to new format
                story.summary = {
                    source: sourceText,
                    target: targetText
                };
                
                // Write back to file
                fs.writeFileSync(filePath, JSON.stringify(story, null, 4) + '\n', 'utf8');
                console.log(`âœ… Updated: ${path.basename(filePath)}`);
                return true;
            } else {
                console.log(`âš ï¸  No translation found for: ${path.basename(filePath)}`);
                console.log(`   Summary: "${sourceText}"`);
                return false;
            }
        } else if (story.summary && typeof story.summary === 'object') {
            console.log(`â„¹ï¸  Already migrated: ${path.basename(filePath)}`);
            return true;
        } else {
            console.log(`â„¹ï¸  No summary: ${path.basename(filePath)}`);
            return true;
        }
    } catch (error) {
        console.error(`âŒ Error processing ${filePath}:`, error.message);
        return false;
    }
}

function migrateAllStories() {
    const files = fs.readdirSync(storiesDir)
        .filter(file => file.endsWith('.json'))
        .map(file => path.join(storiesDir, file));
    
    console.log(`\nğŸ”„ Migrating ${files.length} story files...\n`);
    
    let updated = 0;
    let skipped = 0;
    let errors = 0;
    
    files.forEach(file => {
        const result = updateStoryFile(file);
        if (result === true) updated++;
        else if (result === false) skipped++;
        else errors++;
    });
    
    console.log(`\nğŸ“Š Migration Summary:`);
    console.log(`   âœ… Updated: ${updated}`);
    console.log(`   âš ï¸  Skipped: ${skipped}`);
    console.log(`   âŒ Errors: ${errors}`);
    console.log(`\nâœ¨ Migration complete!\n`);
}

// Run migration
migrateAllStories();
